import React, { useEffect, useState } from "react";
import axios from "axios";

export default function Exam() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [loading, setLoading] = useState(true);
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  // Fetch questions on load
  useEffect(() => {
    async function fetchQuestions() {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5000/api/questions");
        setQuestions(res.data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load questions.");
      } finally {
        setLoading(false);
      }
    }
    fetchQuestions();
  }, []);

  function handleAnswerChange(index, value) {
    setAnswers((prev) => ({
      ...prev,
      ["Q" + (index + 1)]: value,
    }));
  }

  async function handleSubmit() {
    try {
      setStatus("");
      setError("");

      if (questions.length === 0) {
        setError("No questions to submit.");
        return;
      }

      // Simple score calculation: exact match (case-insensitive, trimmed)
      let score = 0;
      questions.forEach((q, idx) => {
        const key = "Q" + (idx + 1);
        const userAns = (answers[key] || "").trim().toLowerCase();
        const correct = (q.answer || "").trim().toLowerCase();
        if (correct && userAns === correct) score++;
      });

      const rollNumber =
        localStorage.getItem("studentRollNumber") || "Unknown Roll";
      const name = localStorage.getItem("studentName") || "Student";

      const payload = {
        rollNumber,
        name,
        answers,
        score,
      };

      const res = await axios.post(
        "http://localhost:5000/api/submit-exam",
        payload
      );

      if (res.data.success) {
        setStatus(
          `✅ Exam submitted successfully! Score: ${score}/${questions.length}`
        );
      } else {
        setError("Failed to submit exam.");
      }
    } catch (err) {
      console.error(err);
      setError("Server error while submitting exam.");
    }
  }

  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h1 style={styles.title}>Online Exam</h1>

        {loading && <p>Loading questions...</p>}
        {error && <p style={styles.error}>{error}</p>}
        {status && <p style={styles.status}>{status}</p>}

        {!loading && !error && questions.length === 0 && (
          <p>No questions available. Contact admin.</p>
        )}

        {/* Questions List */}
        {!loading && questions.length > 0 && (
          <div style={styles.questionsWrapper}>
            {questions.map((q, idx) => (
              <div key={idx} style={styles.questionCard}>
                <p style={styles.questionText}>
                  <b>Q{idx + 1}:</b> {q.question || q.q || "Question text"}
                </p>

                <textarea
                  style={styles.textarea}
                  placeholder="Type your answer here..."
                  value={answers["Q" + (idx + 1)] || ""}
                  onChange={(e) =>
                    handleAnswerChange(idx, e.target.value)
                  }
                />
              </div>
            ))}
          </div>
        )}

        {/* Submit Button */}
        {!loading && questions.length > 0 && (
          <button style={styles.button} onClick={handleSubmit}>
            Submit Exam
          </button>
        )}
      </div>
    </div>
  );
}

const styles = {
  page: {
    background: "#e5e7eb",
    minHeight: "100vh",
    padding: "20px",
  },
  container: {
    maxWidth: "900px",
    margin: "0 auto",
    background: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0px 4px 12px rgba(0,0,0,0.1)",
  },
  title: {
    marginBottom: "16px",
    textAlign: "center",
  },
  questionsWrapper: {
    marginTop: "10px",
    marginBottom: "16px",
  },
  questionCard: {
    marginBottom: "12px",
    padding: "10px",
    borderRadius: "8px",
    background: "#f9fafb",
    border: "1px solid #e5e7eb",
  },
  questionText: {
    marginBottom: "8px",
  },
  textarea: {
    width: "100%",
    minHeight: "70px",
    borderRadius: "6px",
    border: "1px solid #d1d5db",
    padding: "8px",
    resize: "vertical",
  },
  button: {
    padding: "10px 16px",
    background: "#16a34a",
    color: "white",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    display: "block",
    margin: "0 auto",
  },
  error: {
    color: "red",
    marginBottom: "8px",
  },
  status: {
    color: "green",
    marginBottom: "8px",
  },
};
