import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

export default function Exam() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [activeQuestion, setActiveQuestion] = useState(null);
  const [loading, setLoading] = useState(true);
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  const videoRef = useRef(null);

  // ---------------- FETCH QUESTIONS ----------------
  useEffect(() => {
    async function fetchQuestions() {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5000/api/questions");
        setQuestions(res.data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load questions.");
      } finally {
        setLoading(false);
      }
    }
    fetchQuestions();
  }, []);

  // ---------------- SETUP WEBCAM ----------------
  useEffect(() => {
    let stream;
    async function setupCam() {
      try {
        stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 1280, height: 720 },
          audio: false,
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error("Could not start webcam:", err);
        setError("Webcam permission denied or not available.");
      }
    }
    setupCam();

    return () => {
      if (stream) {
        stream.getTracks().forEach((t) => t.stop());
      }
    };
  }, []);

  // ---------------- ANSWER HANDLING ----------------
  function handleAnswerChange(index, value) {
    setAnswers((prev) => ({
      ...prev,
      ["Q" + (index + 1)]: value,
    }));
  }

  // When textarea focused, mark it as active for keyboard typing
  function handleFocusQuestion(index) {
    setActiveQuestion(index);
  }

  // ---------------- VIRTUAL KEYBOARD ----------------
  const keyboardRows = ["QWERTYUIOP", "ASDFGHJKL", "ZXCVBNM"];

  function typeOnActive(key) {
    if (activeQuestion === null) {
      // if nothing is focused, ignore
      return;
    }
    const qKey = "Q" + (activeQuestion + 1);
    const current = answers[qKey] || "";

    if (key === "SPACE") {
      handleAnswerChange(activeQuestion, current + " ");
    } else if (key === "BACKSPACE") {
      handleAnswerChange(activeQuestion, current.slice(0, -1));
    } else if (key === "CLEAR") {
      handleAnswerChange(activeQuestion, "");
    } else {
      handleAnswerChange(activeQuestion, current + key);
    }
  }

  // ---------------- SUBMIT EXAM ----------------
  async function handleSubmit() {
    try {
      setStatus("");
      setError("");

      if (questions.length === 0) {
        setError("No questions to submit.");
        return;
      }

      // Simple score calculation: exact match (case-insensitive, trimmed)
      let score = 0;
      questions.forEach((q, idx) => {
        const key = "Q" + (idx + 1);
        const userAns = (answers[key] || "").trim().toLowerCase();
        const correct = (q.answer || "").trim().toLowerCase();
        if (correct && userAns === correct) score++;
      });

      const rollNumber =
        localStorage.getItem("studentRollNumber") || "Unknown Roll";
      const name = localStorage.getItem("studentName") || "Student";

      const payload = {
        rollNumber,
        name,
        answers,
        score,
      };

      const res = await axios.post(
        "http://localhost:5000/api/submit-exam",
        payload
      );

      if (res.data.success) {
        setStatus(
          `âœ… Exam submitted successfully! Score: ${score}/${questions.length}`
        );
      } else {
        setError("Failed to submit exam.");
      }
    } catch (err) {
      console.error(err);
      setError("Server error while submitting exam.");
    }
  }

  // ---------------- RENDER ----------------
  return (
    <div style={styles.page}>
      <div style={styles.container}>
        <h1 style={styles.title}>Gesture-Based Online Exam</h1>

        {error && <p style={styles.error}>{error}</p>}
        {status && <p style={styles.status}>{status}</p>}

        <div style={styles.layout}>
          {/* LEFT: Webcam + Big Keyboard */}
          <div style={styles.leftPanel}>
            <div style={styles.webcamWrapper}>
              <video
                ref={videoRef}
                autoPlay
                muted
                playsInline
                style={styles.webcam}
              />
            </div>

            {/* Virtual Keyboard - currently click-based, later gesture-controlled */}
            <div id="gesture-keyboard" style={styles.keyboardWrapper}>
              {keyboardRows.map((row, rIndex) => (
                <div key={rIndex} style={styles.keyboardRow}>
                  {row.split("").map((ch, i) => (
                    <div
                      key={i}
                      className="gesture-key"
                      style={styles.key}
                      onClick={() => typeOnActive(ch)}
                    >
                      {ch}
                    </div>
                  ))}
                </div>
              ))}

              {/* Special keys row */}
              <div style={styles.keyboardRow}>
                <div
                  className="gesture-key"
                  style={{ ...styles.key, flex: 2 }}
                  onClick={() => typeOnActive("SPACE")}
                >
                  SPACE
                </div>
                <div
                  className="gesture-key"
                  style={{ ...styles.key, flex: 1 }}
                  onClick={() => typeOnActive("BACKSPACE")}
                >
                  âŒ«
                </div>
                <div
                  className="gesture-key"
                  style={{ ...styles.key, flex: 1 }}
                  onClick={() => typeOnActive("CLEAR")}
                >
                  CLEAR
                </div>
              </div>
            </div>
          </div>

          {/* RIGHT: Questions + Answers */}
          <div style={styles.rightPanel}>
            {loading && <p>Loading questions...</p>}

            {!loading && questions.length === 0 && (
              <p>No questions available. Contact admin.</p>
            )}

            {!loading && questions.length > 0 && (
              <>
                <h2 style={{ marginBottom: 10 }}>Questions</h2>
                <div style={styles.questionsWrapper}>
                  {questions.map((q, idx) => (
                    <div key={idx} style={styles.questionCard}>
                      <p style={styles.questionText}>
                        <b>Q{idx + 1}:</b> {q.question || q.q || "Question"}
                      </p>
                      <textarea
                        style={styles.textarea}
                        placeholder="Type your answer here..."
                        value={answers["Q" + (idx + 1)] || ""}
                        onChange={(e) =>
                          handleAnswerChange(idx, e.target.value)
                        }
                        onFocus={() => handleFocusQuestion(idx)}
                      />
                      {activeQuestion === idx && (
                        <p style={styles.activeHint}>
                          ðŸ”µ Typing with keyboard here
                        </p>
                      )}
                    </div>
                  ))}
                </div>

                <button style={styles.submitBtn} onClick={handleSubmit}>
                  Submit Exam
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    background: "#e5e7eb",
    minHeight: "100vh",
    padding: "20px",
  },
  container: {
    maxWidth: "1200px",
    margin: "0 auto",
    background: "white",
    padding: "20px",
    borderRadius: "10px",
    boxShadow: "0px 4px 12px rgba(0,0,0,0.1)",
  },
  title: {
    marginBottom: "12px",
    textAlign: "center",
  },
  layout: {
    display: "flex",
    gap: "16px",
    marginTop: "10px",
  },
  leftPanel: {
    flex: 1.2,
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  rightPanel: {
    flex: 1,
    maxHeight: "80vh",
    overflowY: "auto",
  },
  webcamWrapper: {
    position: "relative",
    width: "100%",
    paddingBottom: "56.25%", // 16:9
    background: "#000",
    borderRadius: "12px",
    overflow: "hidden",
  },
  webcam: {
    position: "absolute",
    top: 0,
    left: 0,
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
  keyboardWrapper: {
    background: "rgba(249, 250, 251, 0.95)",
    borderRadius: "12px",
    padding: "10px",
    boxShadow: "0 2px 6px rgba(0,0,0,0.08)",
  },
  keyboardRow: {
    display: "flex",
    justifyContent: "center",
    gap: "6px",
    marginBottom: "6px",
  },
  key: {
    flex: 1,
    minWidth: 40,
    padding: "10px 8px",
    background: "#e5e7eb",
    borderRadius: "8px",
    textAlign: "center",
    fontWeight: 700,
    cursor: "pointer",
    userSelect: "none",
  },
  questionsWrapper: {
    marginTop: "4px",
    marginBottom: "8px",
  },
  questionCard: {
    marginBottom: "10px",
    padding: "10px",
    borderRadius: "8px",
    background: "#f9fafb",
    border: "1px solid #e5e7eb",
  },
  questionText: {
    marginBottom: "6px",
  },
  textarea: {
    width: "100%",
    minHeight: "70px",
    borderRadius: "6px",
    border: "1px solid #d1d5db",
    padding: "8px",
    resize: "vertical",
  },
  submitBtn: {
    padding: "10px 16px",
    background: "#16a34a",
    color: "white",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    marginTop: "6px",
  },
  error: {
    color: "red",
    marginBottom: "8px",
  },
  status: {
    color: "green",
    marginBottom: "8px",
  },
  activeHint: {
    fontSize: "12px",
    color: "#2563eb",
    marginTop: "4px",
  },
};
