import React, { useEffect, useRef, useState } from "react";
import axios from "axios";

export default function Exam() {
  const [questions, setQuestions] = useState([]);
  const [answers, setAnswers] = useState({});
  const [activeQuestion, setActiveQuestion] = useState(null);
  const [loading, setLoading] = useState(true);
  const [status, setStatus] = useState("");
  const [error, setError] = useState("");

  const videoRef = useRef(null);

  // ---------------- FETCH QUESTIONS ----------------
  useEffect(() => {
    async function fetchQuestions() {
      try {
        setLoading(true);
        const res = await axios.get("http://localhost:5000/api/questions");
        setQuestions(res.data || []);
      } catch (err) {
        console.error(err);
        setError("Failed to load questions.");
      } finally {
        setLoading(false);
      }
    }
    fetchQuestions();
  }, []);

  // ---------------- SETUP WEBCAM ----------------
  useEffect(() => {
    let stream;
    async function setupCam() {
      try {
        stream = await navigator.mediaDevices.getUserMedia({
          video: { width: 1280, height: 720 },
          audio: false,
        });
        if (videoRef.current) {
          videoRef.current.srcObject = stream;
        }
      } catch (err) {
        console.error("Could not start webcam:", err);
        setError("Webcam permission denied or not available.");
      }
    }
    setupCam();

    return () => {
      if (stream) {
        stream.getTracks().forEach((t) => t.stop());
      }
    };
  }, []);

  // ---------------- ANSWER HANDLING ----------------
  function handleAnswerChange(index, value) {
    setAnswers((prev) => ({
      ...prev,
      ["Q" + (index + 1)]: value,
    }));
  }

  function handleFocusQuestion(index) {
    setActiveQuestion(index);
  }

  // ---------------- VIRTUAL KEYBOARD ----------------
  const keyboardRows = ["QWERTYUIOP", "ASDFGHJKL", "ZXCVBNM"];

  function typeOnActive(key) {
    if (activeQuestion === null) return;

    const qKey = "Q" + (activeQuestion + 1);
    const current = answers[qKey] || "";

    if (key === "SPACE") {
      handleAnswerChange(activeQuestion, current + " ");
    } else if (key === "BACKSPACE") {
      handleAnswerChange(activeQuestion, current.slice(0, -1));
    } else if (key === "CLEAR") {
      handleAnswerChange(activeQuestion, "");
    } else {
      handleAnswerChange(activeQuestion, current + key);
    }
  }

  // ---------------- SUBMIT EXAM ----------------
  async function handleSubmit() {
    try {
      setStatus("");
      setError("");

      if (questions.length === 0) {
        setError("No questions to submit.");
        return;
      }

      let score = 0;
      questions.forEach((q, idx) => {
        const key = "Q" + (idx + 1);
        const userAns = (answers[key] || "").trim().toLowerCase();
        const correct = (q.answer || "").trim().toLowerCase();
        if (correct && userAns === correct) score++;
      });

      const rollNumber =
        localStorage.getItem("studentRollNumber") || "Unknown Roll";
      const name = localStorage.getItem("studentName") || "Student";

      const payload = {
        rollNumber,
        name,
        answers,
        score,
      };

      const res = await axios.post(
        "http://localhost:5000/api/submit-exam",
        payload
      );

      if (res.data.success) {
        setStatus(
          `âœ… Exam submitted successfully! Score: ${score}/${questions.length}`
        );
      } else {
        setError("Failed to submit exam.");
      }
    } catch (err) {
      console.error(err);
      setError("Server error while submitting exam.");
    }
  }

  // ---------------- RENDER ----------------
  return (
    <div style={styles.page}>
      {error && <p style={styles.errorBanner}>{error}</p>}
      {status && <p style={styles.statusBanner}>{status}</p>}

      <div style={styles.fullScreenWrapper}>
        {/* Big Webcam */}
        <video
          ref={videoRef}
          autoPlay
          muted
          playsInline
          style={styles.webcam}
        />

        {/* Dark gradient overlay at bottom (for keyboard contrast) */}
        <div style={styles.bottomOverlay} />

        {/* Virtual Keyboard overlay at bottom */}
        <div id="gesture-keyboard" style={styles.keyboardWrapper}>
          {keyboardRows.map((row, rIndex) => (
            <div key={rIndex} style={styles.keyboardRow}>
              {row.split("").map((ch, i) => (
                <div
                  key={i}
                  className="gesture-key"
                  style={styles.key}
                  onClick={() => typeOnActive(ch)}
                >
                  {ch}
                </div>
              ))}
            </div>
          ))}

          <div style={styles.keyboardRow}>
            <div
              className="gesture-key"
              style={{ ...styles.key, flex: 3 }}
              onClick={() => typeOnActive("SPACE")}
            >
              SPACE
            </div>
            <div
              className="gesture-key"
              style={{ ...styles.key, flex: 1.2 }}
              onClick={() => typeOnActive("BACKSPACE")}
            >
              âŒ«
            </div>
            <div
              className="gesture-key"
              style={{ ...styles.key, flex: 1.2 }}
              onClick={() => typeOnActive("CLEAR")}
            >
              CLEAR
            </div>
          </div>
        </div>

        {/* Questions panel in corner */}
        <div style={styles.questionsPanel}>
          {loading && <p>Loading questions...</p>}

          {!loading && questions.length === 0 && (
            <p>No questions available. Contact admin.</p>
          )}

          {!loading && questions.length > 0 && (
            <>
              <h3 style={styles.panelTitle}>Questions</h3>
              <div style={styles.questionsScroll}>
                {questions.map((q, idx) => (
                  <div key={idx} style={styles.questionCard}>
                    <p style={styles.questionText}>
                      <b>Q{idx + 1}:</b> {q.question || q.q || "Question"}
                    </p>
                    <textarea
                      style={styles.textarea}
                      placeholder="Type your answer here..."
                      value={answers["Q" + (idx + 1)] || ""}
                      onChange={(e) =>
                        handleAnswerChange(idx, e.target.value)
                      }
                      onFocus={() => handleFocusQuestion(idx)}
                    />
                    {activeQuestion === idx && (
                      <p style={styles.activeHint}>
                        ðŸ”µ Keyboard typing here
                      </p>
                    )}
                  </div>
                ))}
              </div>

              <button style={styles.submitBtn} onClick={handleSubmit}>
                Submit Exam
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

const styles = {
  page: {
    background: "#000",
    minHeight: "100vh",
    margin: 0,
    padding: 0,
    position: "relative",
    color: "#fff",
    fontFamily: "sans-serif",
  },
  fullScreenWrapper: {
    position: "relative",
    width: "100vw",
    height: "100vh",
    overflow: "hidden",
  },
  webcam: {
    width: "100%",
    height: "100%",
    objectFit: "cover",
  },
  bottomOverlay: {
    position: "absolute",
    left: 0,
    right: 0,
    bottom: 0,
    height: "30vh",
    background:
      "linear-gradient(to top, rgba(0,0,0,0.8), rgba(0,0,0,0.2), transparent)",
    pointerEvents: "none",
  },
  keyboardWrapper: {
    position: "absolute",
    left: "50%",
    bottom: "1.5rem",
    transform: "translateX(-50%)",
    width: "90%",
    maxWidth: "1000px",
    padding: "10px",
    borderRadius: "16px",
    background: "rgba(15,23,42,0.92)",
    boxShadow: "0 8px 25px rgba(0,0,0,0.6)",
  },
  keyboardRow: {
    display: "flex",
    justifyContent: "center",
    gap: "4px",
    marginBottom: "4px",
    flexWrap: "wrap",
  },
  key: {
    flex: 1,
    minWidth: 32,
    maxWidth: 60,
    padding: "8px 6px",
    background: "#e5e7eb",
    borderRadius: "8px",
    textAlign: "center",
    fontWeight: 700,
    fontSize: "14px",
    cursor: "pointer",
    userSelect: "none",
    color: "#111827",
  },
  questionsPanel: {
    position: "absolute",
    top: "1.25rem",
    right: "1.25rem",
    width: "320px",
    maxHeight: "75vh",
    padding: "12px",
    borderRadius: "16px",
    background: "rgba(255,255,255,0.96)",
    color: "#111827",
    boxShadow: "0 8px 25px rgba(0,0,0,0.5)",
    display: "flex",
    flexDirection: "column",
  },
  questionsScroll: {
    marginTop: "6px",
    overflowY: "auto",
    paddingRight: "4px",
  },
  questionCard: {
    marginBottom: "8px",
    padding: "6px",
    borderRadius: "8px",
    background: "#f9fafb",
    border: "1px solid #e5e7eb",
  },
  questionText: {
    marginBottom: "4px",
    fontSize: "14px",
  },
  textarea: {
    width: "100%",
    minHeight: "55px",
    borderRadius: "6px",
    border: "1px solid #d1d5db",
    padding: "6px",
    resize: "vertical",
    fontSize: "13px",
  },
  submitBtn: {
    marginTop: "6px",
    padding: "8px 12px",
    background: "#16a34a",
    color: "white",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    alignSelf: "flex-end",
    fontSize: "14px",
  },
  activeHint: {
    fontSize: "11px",
    color: "#2563eb",
    marginTop: "3px",
  },
  errorBanner: {
    position: "absolute",
    top: 8,
    left: "50%",
    transform: "translateX(-50%)",
    background: "rgba(239,68,68,0.9)",
    padding: "6px 12px",
    borderRadius: "999px",
    zIndex: 30,
    fontSize: "13px",
  },
  statusBanner: {
    position: "absolute",
    top: 8,
    left: "50%",
    transform: "translateX(-50%)",
    background: "rgba(22,163,74,0.9)",
